import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { FaAngleDoubleRight } from "react-icons/fa";

import "./popUpStyles/serviceBooking.css";
import { CompanyServiceList } from "../../companyData";

function ServiceBooking(props) {
  const [viewService, setViewService] = useState(false);
  const { CompanyListDataArray } = useContext(CompanyServiceList);
  function arrText(index) {
    const texttest = CompanyListDataArray[index].serviceList;
    let result = texttest.map((x) => {
      return <p>{x}</p>;
    });
    return result;
  }
  let numb = 1;
  function clearAbutton() {
    console.log("clear button clicked");
  }
  function bookButton() {
    if (!viewService) {
      setViewService(true);
    }
  }
  function bookNowPage() {
    if (viewService) {
      setViewService(false);
    }
  }
  return (
    <div className="popUp-serviceWrapper">
      <div className="serviceLink">
        <div className="serviceLinkwrapper">
          <p>
            <Link to="/" className="popupLink linkText">
              Home
            </Link>
          </p>
          <FaAngleDoubleRight className="iconlink" />
          <button className="linkText" onClick={bookNowPage}>
            Book Service
          </button>
        </div>
        <div className="serviceLinkwrapper">
          <Link to="/">Cancel</Link>
        </div>
      </div>

      <div className="serviceSectionwrapper">
        {/* SELECET SECTION */}
        <div className={viewService ? "deActiveBooking" : "popUpserviceContent"}>
          .popUp
        </div>
        {/* SECOND SECTION */}
        {/* CLEANING SERVICE EACH */}
        <div
          className={
            !viewService ?  "deActiveBooking": "selectedCleaningService" 
          }>
          <div className="selectedCleaningServiceWrapper">
            <div className="serviceCompanyProperty">
              <div className="sc-imageContainer">
                <img
                  src="https://images.pexels.com/photos/4107120/pexels-photo-4107120.jpeg?auto=compress&cs=tinysrgb&w=600"
                  alt=""
                />
              </div>
              <div className="sc-nameAndAbout">
                <h3>JRUN CLEANING SERVICE</h3>

                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  Amet, odit? Assumenda beatae blanditiis at nobis nisi magnam
                  facere autem porro enim explicabo sunt alias nam asperiores
                  tenetur, dolorum harum omnis?
                </p>
              </div>
              <div className="sc-serviceRendered">{arrText(numb)}</div>
            </div>
            {/* STAFF PROPS */}
            <div className="companyStaffDetails">
              <div className="sc-staffers">
                <p>Staffers</p>
                <div className="sc-staffImagesContainer">
                  <img
                    src="https://images.pexels.com/photos/7752788/pexels-photo-7752788.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
                    alt="working staff"
                  />
                </div>
                <p>Working hours</p>
                <div className="workingHours">
                  <p className="day">Monday</p>
                  <div className="time">
                    <p className="start">
                      9:00 <span>AM</span>
                    </p>
                    <span>-</span>
                    <p className="close">
                      5:00 <span>PM</span>
                    </p>
                  </div>
                </div>
              </div>
              <div className="sc-bookNowButton">Book Now</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ServiceBooking;
