import React, { useState } from "react";
import "./style/slider.css";

const customersReview = [
  {
    id: 1,
    text: "Card 1",
  },
  {
    id: 2,
    text: "Card 2",
  },
  {
    id: 3,
    text: "Card 3",
  },
  {
    id: 4,
    text: "Card 4",
  },
  {
    id: 5,
    text: "Card 5",
  },
];
function SliderCarousel() {
  const [currentIndex, setCurrentIndex] = useState(5);

  return (
    <>
      <div className="sliderContainer">
        {customersReview.slice(0, currentIndex).map((item, currentIndex) => {
          const { text } = item;
          for (let i = 0; i <= customersReview.length; i++) {
            if (currentIndex) {
              console.log(customersReview[currentIndex]);
            }
          }
          return (
            <div className="c-reviews" key={currentIndex}>
              <h3>{text}</h3>
            </div>
          );
        })}
      </div>
    </>
  );
}
export default SliderCarousel;
