import React, { useState } from "react";
import "./style/slider.css";

const customersReview = [
  {
    id: 1,
    text: "Card",
  },
  {
    id: 2,
    text: "Card",
  },
  {
    id: 3,
    text: "Card",
  },
  {
    id: 4,
    text: "Card",
  },
  {
    id: 5,
    text: "Card",
  },
];
function SliderCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);
  let cardIndex =0
  for (let i = 0; i <= customersReview.length; i++) {
    if (cardIndex  === currentIndex) {
      console.log(customersReview[currentIndex]);
    }
    // return customersReview[i];
  }

  return (
    <>
      <div className="sliderContainer">
        {customersReview.map((item) => {
          const { id, text } = item;
          return (
            <div className="c-reviews" key={id}>
              <h3>{text}</h3>
            </div>
          );
        })}
      </div>
    </>
  );
}
export default SliderCarousel;
