import React, { useState } from "react";
import { FaAngleLeft, FaAngleRight } from "react-icons/fa";
// Styles
import "./popUpStyles/payment.css";

function PaymentComponent() {
  const [page, setPage] = useState(1);
  const [formValues, setFormValues] = useState({
    address: "",
    phone: "",
    email: "",
    name: "",
    type: "",
    date: "",
    time: "",
  });

  function confirmPage() {
    if (formValues.time && formValues.date) {
      console.log(true);
    } else {
      console.log(false);
    }
  }

  const handleChange = (event) => {
    const { name, address, date, email, type, phone, value } = event.target;
    setFormValues({
      ...formValues,
      [address]: value,
      [name]: value,
      [type]: value,
      [date]: value,
      [email]: value,
      [phone]: value,
      [address]: value,
    });

    console.log(formValues);
  };

  function backBtn() {
    setPage(page - 1);
  }
  function nextBtn() {
    setPage(page + 1);
  }

  function progressBtn() {
    return (
      <>
        <div className="progressButton">
          <button type="button" className="prevBtn" onClick={backBtn}>
            <FaAngleLeft />
            BACK
          </button>
          <button type="button" className="nextBtn" onClick={nextBtn}>
            NEXT <FaAngleRight />
          </button>
        </div>
        {/* <input type="submit" value="Confirm" className="formSubmitBtn" /> */}
      </>
    );
  }

  return (
    <div className="bookingPaymentPopUpWrapper">
      <div className="bookingPaymentWrapper">
        <div className="bookingPaymentProgressBar">
          <div className="progressHandlerContainer">
            <div className="booking details">
              <span>1</span>
              <h4>Details</h4>
            </div>
            <div className="booking dateAndTime">
              <span>2</span>
              <h4>Date & Time</h4>
            </div>
            <div className="booking review">
              <span>3</span>
              <h4>Review</h4>
            </div>
            <div className="booking payment">
              <span>4</span>
              <h4>Payment</h4>
            </div>
          </div>
          {/* ---------------------------------------------------------------------- */}
          <div className="bookingPaymentFormContainer">
            <div className="inputWrapper">
              <form method="get">
                <div className="formDataHandler">
                  {/* ---------------------------------------------------------------*/}

                  <div
                    className={
                      page === 1
                        ? "detailsContainer absolute"
                        : "detailsContainer"
                    }>
                    <fieldset>
                      <legend>Name</legend>
                      <input
                        type="text"
                        name="name"
                        value={formValues.name}
                        onChange={handleChange}
                      />
                    </fieldset>

                    <fieldset>
                      <legend>Email</legend>
                      <input
                        type="email"
                        name="email"
                        value={formValues.email}
                        onChange={handleChange}
                      />
                    </fieldset>

                    <fieldset>
                      <legend>Phone</legend>
                      <input
                        type="tel"
                        name="phone"
                        value={formValues.phone}
                        onChange={handleChange}
                      />
                    </fieldset>

                    <fieldset>
                      <legend>Address</legend>
                      <input
                        type="text"
                        name="address"
                        value={formValues.address}
                        onChange={handleChange}
                      />
                    </fieldset>

                    <fieldset>
                      <legend>Building type</legend>
                      <input
                        type="text"
                        name="type"
                        value={formValues.type}
                        onChange={handleChange}
                      />
                    </fieldset>
                  </div>

                  {/* ---------------------------------------------------------------*/}
                  <div
                    className={
                      page === 2
                        ? "dateAndTimeHandler absolute"
                        : "dateAndTimeHandler"
                    }>
                    <fieldset className="dateWrapper">
                      <legend>Select Date</legend>
                      <input type="date" name="email" />
                    </fieldset>
                    <fieldset className="selectTime">
                      <legend>Select Time</legend>
                      <select name="time" id="time">
                        <option value="9:00Am">9:00Am</option>
                        <option value="11:00Am">11:00Am</option>
                        <option value="1:00Pm">1:00Pm</option>
                        <option value="3:00Pm">3:00Pm</option>
                      </select>
                    </fieldset>
                  </div>
                  {/* -------------------------------------------------------------- */}
                  {confirmPage && (
                    <div className="bookingDetailsReview absolute">
                      {/* className={
                        page === 3
                          ? "bookingDetailsReview absolute"
                          : "bookingDetailsReview"
                      }> */}

                      <div className="reviewWrapper">
                        <div className="activeCompany">
                          <h3>JRUN Ceaning Service</h3>
                        </div>
                        <div className="activeCustomerInfo">
                          <fieldset className="customerDetails">
                            <legend>Details</legend>
                            <div className="customer">
                              <p className="s-header">Name</p>
                              <h3 className="c-clientInput">nelly pixu</h3>
                            </div>
                            <div className="customer">
                              <p className="s-header">Address</p>
                              <h3 className="c-clientInput">
                                606 washington Dc
                              </h3>
                            </div>
                            <div className="customer">
                              <p className="s-header">Phone</p>
                              <h3 className="c-clientInput">0123456789</h3>
                            </div>
                          </fieldset>
                          <fieldset>
                            <legend>Date And Time</legend>
                            <div className="customer">
                              <p className="s-header">Date</p>
                              <h3 className="c-clientInput">
                                16th october 2022
                              </h3>
                            </div>
                            <div className="customer">
                              <p className="s-header">Time</p>
                              <h3 className="c-clientInput">9:00 AM</h3>
                            </div>
