import React, { useState } from "react";
import Carousel from "react-elastic-carousel";

import "./style/slider.css";

const customersReview = [
  {
    id: 1,
    name: "John Doe",
    imgSrc:
      "https://images.pexels.com/photos/7543641/pexels-photo-7543641.jpeg?auto=compress&cs=tinysrgb&w=600",
    text: "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Voluptate assumenda optio qui est libero labore cum nemo odio accusantium impedit.Card 1",
    rating: 5.0,
  },
  {
    id: 2,
    name: "Katy  Doe",
    imgSrc:
      "https://images.pexels.com/photos/6474814/pexels-photo-6474814.jpeg?auto=compress&cs=tinysrgb&w=600",
    text: "Amazing cleaning service, top notch delivary would definately recommend a great service like this to a friend",
    rating: 5.0,
  },
];
function SliderCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);
  let lastSlide = customersReview.length;

  function prevButton() {
    if (currentIndex <= lastSlide - 1 && currentIndex > 0) {
      return setCurrentIndex(() => {
        return currentIndex - 1;
      });
    } else if (currentIndex === 0) {
      setCurrentIndex(() => {
        return lastSlide - 1;
      });
    }
  }
  
  function nextButton() {
    if (currentIndex < lastSlide - 1) {
      return setCurrentIndex(() => {
        return currentIndex + 1;
      });
    } else if (currentIndex === lastSlide - 1) {
      setCurrentIndex(() => {
        return 0;
      });
    }
  }
   const breakPoints = [
     { width: 1, itemsToShow: 1 },
     {
       width: 550,
       itemsToShow: 2,
       itemsToScroll: 2,
       pagination: true,
     },
     { width: 850, itemsToShow: 4 },
     {
       width: 1150,
       itemsToShow: 5,
       itemsToScroll: 2,
     },
     { width: 1450, itemsToShow: 5 },
     { width: 900, itemsToShow: 6 },
   ];

  return (
    <>
      <div className="sliderContainer">
        <div className="carouselBtn">
          <div className="card-btnCon">
            <button type="button" className="prevButton" onClick={prevButton}>
              &#10096;
            </button>
          </div>
          <div className="card-btnCon">
            <button type="button" className="nextButton" onClick={nextButton}>
              &#10097;
            </button>
          </div>
        </div>

        <div className="reviewShadowBox">
          <h3>Here is what some of our customers have to say</h3>
        </div>
        <Carousel breakPoints={breakPoints}>
          {customersReview.map((item, slideIndex) => {
            const { text, imgSrc, name, rating, id } = item;
            return (
              <div key={slideIndex} className="textContainer">
                <div className="cardContentWrapper" key={id}>
                  <div className="avatarWrapper">
                    <div className="avatar">
                      <img src={imgSrc} alt="profile" />
                    </div>
                  </div>
                  <div className="nameAndRatingContainer">
                    <div className="nameContainer">{name}</div>
                    <div className="rating" id="rating ">
                      {rating}
                    </div>
                  </div>
                  <div className="customerReviewContext">
                    <h3 className="texts">{text}</h3>
                  </div>
                </div>
              </div>
            )
          })}
        </Carousel>
      </div>
    </>
  );
}
export default SliderCarousel;
