import React, { useState } from "react";
import "./style/slider.css";

const customersReview = [
  {
    id: 1,
    text: "Card 1",
  },
  {
    id: 2,
    text: "this is Card 2",
  },
  {
    id: 3,
    text: "Card number 3",
  },
  {
    id: 4,
    text: "Card section 4",
  },
  {
    id: 5,
    text: "Card 5",
  },
];
function SliderCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);
  let lastSlide = customersReview.length;

  function prevButton() {
    console.log("prev clicked");
  }
  function nextButton() {
    if (currentIndex <= lastSlide) {
      return setCurrentIndex(() => {
        return (currentIndex + 1);
      });
    }
    console.log(currentIndex);
  }

  return (
    <>
      <div className="sliderContainer">
        <div className="carouselBtn">
          <div className="card-btnCon">
            <button type="button" className="prevButton" onClick={prevButton}>
              prev
            </button>
          </div>
          <div className="card-btnCon">
            <button type="button" className="nextButton" onClick={nextButton}>
              next
            </button>
          </div>
        </div>

        {customersReview.slice("").map((item, slideIndex) => {
          return (
            <div key={slideIndex} className="textContainer">
              {currentIndex === slideIndex && (
                <h3 className="texts">{item.text}</h3>
              )}
            </div>
          );
        })}
      </div>
    </>
  );
}
export default SliderCarousel;
